import React from 'react';
import './Header.css';

const Header = () => {
  const [isOpen, setIsOpen] = React.useState(false);
  return (
    <header className="header">
      <img src="logo.png" alt="Logo" className="logo" />
      <nav className="nav-menu">
        <button className="nav-menu-button" onClick={() => setIsOpen(!isOpen)}>
          â˜°
        </button>
        <div className={isOpen ? "nav-menu-items" : "nav-menu-items hidden"}>
          <a href="#about" className="nav-link" onClick={() => setIsOpen(false)}>ABOUT</a>
          <a href="#services" className="nav-link" onClick={() => setIsOpen(false)}>SERVICES</a>
          <a href="#contact" className="nav-link" onClick={() => setIsOpen(false)}>CONTACT</a>
          <a href="#subscribe" className="nav-link" onClick={() => setIsOpen(false)}>SUBSCRIBE</a>
        </div>
      </nav>
    </header>
  );
};

export default Header;
