import React, { useEffect, useState } from 'react';
import './Preloader.css';

const Preloader = () => {
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        window.onload = () => {
            setLoading(false);
        }
    }, []);

    return (
        loading && (
            <div className="preloader">
                <div className="loader"></div>
            </div>
        )
    );
};

export default Preloader;
